import React, { useState } from "react";
import { enrollInInsurance } from "../services/financeService";

const Insurance = () => {
  const [insurancePlan, setInsurancePlan] = useState("");
  const [message, setMessage] = useState("");

  // Get the user's name from localStorage
  const userName = localStorage.getItem("userName");

  const handleEnroll = async () => {
    const response = await enrollInInsurance(insurancePlan);
    setMessage(`Dear ${userName}, you have successfully enrolled in the ${insurancePlan} insurance plan.`);
  };

  return (
    <div>
      <h2>Enroll in Insurance</h2>
      <input
        type="text"
        placeholder="Insurance Plan"
        value={insurancePlan}
        onChange={(e) => setInsurancePlan(e.target.value)}
      />
      <button onClick={handleEnroll}>Enroll</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Insurance;
