import React, { useState } from "react";
import { applyForLoan } from "../services/financeService";

const Loans = () => {
  const [loanAmount, setLoanAmount] = useState("");
  const [tenure, setTenure] = useState("");
  const [message, setMessage] = useState("");

  // Get the user's name from localStorage
  const userName = localStorage.getItem("userName");

  const handleApplyLoan = async () => {
    const response = await applyForLoan(loanAmount, tenure);
    setMessage(`Loan of ${loanAmount} approved for User ${userName} for a tenure of ${tenure} months.`);
  };

  return (
    <div>
      <h2>Apply for a Loan</h2>
      <input
        type="number"
        placeholder="Loan Amount"
        value={loanAmount}
        onChange={(e) => setLoanAmount(e.target.value)}
      />
      <input
        type="number"
        placeholder="Tenure (months)"
        value={tenure}
        onChange={(e) => setTenure(e.target.value)}
      />
      <button onClick={handleApplyLoan}>Submit</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Loans;
