import React, { useState } from "react";
import { createSavingsAccount } from "../services/financeService";

const Savings = () => {
  const [initialDeposit, setInitialDeposit] = useState("");
  const [message, setMessage] = useState("");

  // Get the user's name from localStorage
  const userName = localStorage.getItem("userName");

  const handleCreateAccount = async () => {
    const response = await createSavingsAccount(initialDeposit);
    setMessage(`Dear ${userName}, your savings account has been created with an initial deposit of ${initialDeposit}.`);
  };

  return (
    <div>
      <h2>Create a Savings Account</h2>
      <input
        type="number"
        placeholder="Initial Deposit"
        value={initialDeposit}
        onChange={(e) => setInitialDeposit(e.target.value)}
      />
      <button onClick={handleCreateAccount}>Create Account</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default Savings;
